output$contents <- renderTable({
inFile <- input$file1
if (is.null(inFile))
return(NULL)
read.csv(inFile$datapath, header = input$header)
})

comment_save <- eventReactive(input$save,{
cat("saveButton was pushed"
)})
output$comment_button <- renderText({
comment_save()
})

output$txt <- renderText({
icons <- paste(input$icons,collapse = ", ");paste("You chose", icons)
})

output$myImages <- renderImage({
if(!("rlogo.png" %in% list.files(getwd()))){
image_read("https://www.r-project.org/logo/Rlogo.png") %>% image_write("rlogo.png")
}
list(src = "rlogo.png",filetype = "image/png",width = 300,height = 300,alt = "This is alternate text")}, deleteFile = FALSE)

output$myImage <- renderImage({
if("ebi_miso.JPG" %in% list.files(getwd())){
list(src = "ebi_miso.JPG",
filetype = "image/jpeg",
width = 400,
height = 300,
alt = "ramen")
}
}, deleteFile = FALSE)
output$info <- renderText({
paste0("x=",input$image_click$x,"y=",input$image_click$y)
})

output$plot1 <- renderPlot({
ggplot(iris,aes(Sepal.Width,Sepal.Length))+geom_point()
})
output$info2 <- renderText({
paste0("x=", input$plot_click$x, "
y=", input$plot_click$y)
})

output$diagram <- renderGrViz({
nodes <- create_node_df(3)
edges <- create_edge_df(from = 1:3,to = c(2,3,1))
create_graph(nodes,edges) %>% render_graph()
})
